def get_metrics(gold, tests):

    for test_name, test_data in tests.items():
        print("########",test_name,"########")
        
        test_len = len(test_data)
        correct = 0
        missing = []

        for word in gold:
            if word in test_data:
                correct +=1
                test_data.remove(word)
            elif word not in test_data:
                missing.append(word)


        print("Len gold", len(gold), "; correct", correct)
        print("Len gold", len(gold))
        print("Len test", test_len)
        # print(correct/len(gold))
        # print("missing",missing)
        # print(len(missing))
        # print("fp", len(test_data), "out of", test_len)

        prec = correct/(correct+(test_len-correct))
        print("prec", prec)

        rec = correct/(correct+len(missing))
        print("rec", rec)

        F1 = 2 * (prec * rec) / (prec + rec)
        print("F1", F1)


